mapper.insert:
update shop 
set shop_owner=#{shop_owner}, shop_number=#{shop_number}, shop_name=#{shop_name}, shop_addr=#{shop_addr}, shop_tel=#{shop_tel}, shop_time=#{shop_time}, shop_photo=#{shop_photo}, shop_etc=#{shop_etc}, 
shop_small_price=#{shop_small_price}, shop_medium_price=#{shop_medium_price}, shop_large_price=#{shop_large_price}, shop_content=#{shop_content}
where shop_seq=#{shop_seq}

mapper.select:
select shop_seq, design_seq, design_price, design_name, design_time
from shop_designer
where shop_seq = #{shop_seq}
